import React from "react";
import "./moviedetails.css";
import backgroundimg from "../../Images/slider-Banner.jpg";
import sliderControl from "../../Images/slider-control.jpg";
import star from "../../Images/star.png"

function MovieDetails() {
  return (
    <div className="movie-detail">
      <div
        className="backdrop-image"
        style={{
          background: `url(${backgroundimg})`,
        }}
      ></div>
      <figure className="poster-box movie-poster">
        <img src={sliderControl} className="img-cover" alt="Marvel" />
      </figure>
      <div className="detail-box">
        <div className="detail-content">
          <h1 className="heading"> Avengers : Endgame</h1>
          <div className="meta-list">
            <div className="meta-item">
              <img src={star} width="20" height="20" alt="rating"/>
              <span className="span">8.4</span>
            </div>
            <div className="separator"></div>
            <div className="meta-item">162m</div>
            <div className="separator"></div>
            <div className="meta-item">2019</div>
            <div className="meta-item card-badge">PG-13</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MovieDetails;
