import React, { useEffect, useState } from "react";
import "./sidebar.css";
import { Link } from "react-router-dom";
import { api_key, fetchDataFromServer } from "../../Api";


const Sidebar = () => {
  const [genrelist, setGenreList] = useState({});

  useEffect(() => {
    fetchDataFromServer(`https://api.themoviedb.org/3/genre/movie/list?api_key=${api_key}`, ({ genres }) => {
      const updatedGenreList = {};
      for (const { id, name } of genres) {
        updatedGenreList[id] = name;
      }
      setGenreList(updatedGenreList);
    });
  }, []);

  return (
    <nav className="sidebar" sidebar>
      <div className="sidebar-inner">
        <div className="sidebar-list">
          <p className="title">Genre</p>
          {Object.entries(genrelist).map(([id, name]) => (
            <Link key={id} to={`./movie-list.html?genre=${id}`} menu-close className="sidebar-link">
              {name}
            </Link>
          ))}

          <p className="title">Language</p>
          <Link to="./movie-list.html" menu-close className="sidebar-link">
            English
          </Link>
          <Link to="./movie-list.html" menu-close className="sidebar-link">
            Hindi
          </Link>
          <div className="overlay" overlay menu-togglers></div>
        </div>
      </div>
    </nav>
  );
};

export default Sidebar;
