import React from "react";
import "./sidebar.css";
import { Link } from "react-router-dom";
import { api_key, fetchDataFromServer } from "../../Api";

function Sidebar() {
  const genrelist = {};

  fetchDataFromServer(
    `https://api.themoviedb.org/3/genre/movie/list?api_key=${api_key}`,
    function ({ genres }) {
      for (const { id, name } of genres) {
        genrelist[id] = name;
      }
      genreLink();
    }
  );

  const sidebarInner = document.createElement("div");
  sidebarInner.classList.add("sidebar-inner");

  sidebarInner.innerHTML = ``;

  const genreLink = function () {
    for (const [genreId, genreName] of Object.entries(genrelist)) {
      const link = document.createElement("a");
      link.classList.add("sidebar-link");
      link.setAttribute("to", "/movie-list");
      link.setAttribute("menu-close", "");
      // link.setAttribute(
      //   "onclick",
      //   `getMovieList("with_genres=${genreId}", "${genreName}")`
      // );
      link.textContent = genreName;
      sidebarInner.querySelectorAll(".sidebar-list")[0].appendChild(link);
    }

    const sidebar = document.querySelector("[sidebar]");
    sidebar.appendChild(sidebarInner);
    togglesidebar(sidebar);
  };

  const togglesidebar = function (Sidebar) {
    const sidebar = document.querySelector("[menu-btn]");
    const sidebarTogglers = document.querySelectorAll("[menu-toggler]");
    const sidebarClose = document.querySelectorAll("[menu-close]");
    const overlay = document.querySelector("[overlay]");

    addEventOnElements(sidebarTogglers, "click", function () {
      sidebar.classList.toggle("active");
      // sidebarBtn.classList.toggle("active")
      overlay.classList.toggle("active");
    });

    addEventOnElements(sidebarClose, "click", function () {
      sidebar.classList.remove("active");
      // sidebarBtn.classList.toggle("active")
      overlay.classList.remove("active");
    });
  };

  return (
    <nav className="sidebar active" sidebar>
      <div className="sidebar-inner">
        <div className="sidebar-list">
          <p className="title">Genre</p>
          <Link to="./movie-list" menu-close className="sidebar-link">
            Action
          </Link>
          <Link to="./movie-list" menu-close className="sidebar-link">
            Horror
          </Link>
          <Link to="./movie-list" menu-close className="sidebar-link">
            Comedy
          </Link>
          <Link to="./movie-list" menu-close className="sidebar-link">
            Adventure
          </Link>
          <Link to="./movie-list" menu-close className="sidebar-link">
            Drama
          </Link>
          <Link to="./movie-list" menu-close className="sidebar-link">
            Sci-fi
          </Link>

          <p className="title">Language</p>
          <Link to="./movie-list.html" menu-close className="sidebar-link">
            English
          </Link>
          <Link to="./movie-list.html" menu-close className="sidebar-link">
            Hindi
          </Link>
          <div className="overlay " overlay menu-togglers></div>
        </div>
      </div>
    </nav>
  );
}

export default Sidebar;
