import React, { useState } from "react";
import { Link } from "react-router-dom";
import Logo from "../../Images/icon.png";
import search from "../../Images/search.png";
import close from "../../Images/close.png";
import menuclose from "../../Images/menu-close.png";
import menu from "../../Images/menu.png";
import "./header.css";
import { api_key, fetchDataFromServer } from "../../Api";

function Header({
  isActive,
  handleToggleClick,
  handleSidebarClick,
  handleSearchResults,
}) {
  const [searchQuery, setSearchQuery] = useState("");

  let searchTimeout;

  const handleSearchInput = (event) => {
    const query = event.target.value;
    setSearchQuery(query);

    clearTimeout(searchTimeout);
    searchTimeout = setTimeout(() => {
      if (query.trim()) {
        fetchDataFromServer(
          `https://api.themoviedb.org/3/search/movie?api_key=${api_key}&query=${query}&page=1&include_adult=false`,
          function ({ results: movieList }) {
            handleSearchResults(movieList);
          }
        );
      }
    }, 500);
  };

  return (
    <div className="header">
      <Link to="/" className="logo">
        <img src={Logo} width="60" height="32" alt="Flicks home" />
        CINESCOPE
      </Link>
      <div className={`search-box ${isActive ? "active" : ""}`} search-box>
        <div className="search-wrapper" search-wrapper>
          <input
          type="text"
          name="search"
          aria-label="search movies"
          className="search-field"
          placeholder="search movies..."
          autoComplete="off"
          value={searchQuery}
          onChange={handleSearchInput}
          />
          <img
            src={search}
            width="24"
            height="24"
            alt="search"
            className="leading-icon"
          />
        </div>
        <button className="search-btn" onClick={handleToggleClick}>
          <img src={close} width="24" height="24" alt="close search box" />
        </button>
      </div>
      <button className="search-btn" onClick={handleToggleClick}>
        <img src={search} width="24" height="24" alt="open search box" />
      </button>

      <button className="menu-btn" onClick={handleSidebarClick}>
        <img
          src={menu}
          width="24"
          height="24"
          alt="open-menu"
          className={`open ${isActive ? "" : ""}`}
        />
      </button>
    </div>
  );
}

export default Header;
