import React, { useState } from "react";
import { Link } from "react-router-dom";
import Logo from "../../Images/icon.png";
import search from "../../Images/search.png";
import close from "../../Images/close.png";
import menuclose from "../../Images/menu-close.png";
import menu from "../../Images/menu.png";
import "./header.css";
import Sidebar from "../Sidebar/Sidebar"; // Import the Sidebar component

function Header() {
  const [isActive, setIsActive] = useState(false);

  const handleToggleClick = () => {
    setIsActive((prevState) => !prevState);
  };

  return (
    <div className="header">
      <Link to="" className="logo">
        <img src={Logo} width="60" height="32" alt="Flicks home" />
        CINESCOPE
      </Link>
      <div className={`search-box ${isActive ? "active" : ""}`} search-box>
        <div className="search-wrapper" search-wrapper>
          <input
            type="text"
            name="search"
            aria-label="search movies"
            className="search-field"
            placeholder="search movies..."
            autoComplete="off"
            search-field
          />
          <img
            src={search}
            width="24"
            height="24"
            alt="search"
            className="leading-icon"
          />
        </div>
        <button className="search-btn" onClick={handleToggleClick}>
          <img src={close} width="24" height="24" alt="close search box" />
        </button>
      </div>
      <button className="search-btn" onClick={handleToggleClick}>
        <img src={search} width="24" height="24" alt="open search box" />
      </button>

      <button className="menu-btn" onClick={handleToggleClick}>
        <img
          src={menu}
          width="24"
          height="24"
          alt="open-menu"
          className={`open ${isopen ? "" : "hide"}`}
        />
        <img
          src={menuclose}
          width="24"
          height="24"
          alt="close-menu"
          className={`close ${isActive ? "" : "hide"}`}
        />
      </button>

      {/* Pass the isActive state to the Sidebar component */}
      <Sidebar isActive={isopen} />
    </div>
  );
}

export default Header;
