import React, { useEffect, useState } from "react";
import star from "../../Images/star.png";
import { api_key, fetchDataFromServer } from "../../Api";
import "./search.css"

function Search() {
    const [searchResults, setSearchResults] = useState([]);
  
    useEffect(() => {
      fetchSearchResults();
    }, []);
  
    const fetchSearchResults = async () => {
      try {
        // Replace this with your actual API call
        const response = {
          page: 1,
          results: [
            // ... API response results here
          ],
          total_pages: 44,
          total_results: 869,
        };
  
        setSearchResults(response.results);
      } catch (error) {
        console.error("Error fetching search results:", error);
      }
    };
  
    return (
      <div className="search-modal active">
        <p className="label">Results for</p>
        <h1 className="heading">Hello</h1>
        <div className="grid-list">
          {searchResults.map((movie, index) => (
            <div key={index} className="movie-card movie-card-small">
              <figure className="poster-box card-banner">
                <img
                  src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
                  alt={movie.title}
                  className="img-cover"
                />
              </figure>
              <h4 className="title">{movie.title}</h4>
              <div className="meta-list">
                <div className="meta-item">
                  <img
                    src={star}
                    alt="rating"
                    width="20"
                    height="20"
                    loading="lazy"
                  />
                  <span className="span">{movie.vote_average}</span>
                </div>
                <div className="card-badge">
                  {movie.release_date ? movie.release_date.slice(0, 4) : ""}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
  
  export default Search;
