import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./moviedetails.css";
import sliderControl from "../../Images/slider-control.jpg";
import star from "../../Images/star.png";
import { fetchDataFromServer } from "../../Api";

function MovieDetails() {
  const { movieId } = useParams();
  const [movieData, setMovieData] = useState(null);

  const getGenres = function (genreList) {
    const newGenreList = [];

    for (const { name } of genreList) newGenreList.push(name);
    return newGenreList.join(", ");
  };

  const getCasts = function (castList) {
    const newCastList = [];

    for (let i = 0, len = castList.length; i < len && i < 10; i++) {
      const { name } = castList[i];
      newCastList.push(name);
    }
    return newCastList.join(", ");
  };

  const getDirectors = function (crewList) {
    const Directors = crewList.filter(({ job }) => job === "Director");

    const directorList = [];
    for (const { name } of Directors) directorList.push(name);

    return directorList.join(", ");
  };

  const filterVideos = function (videoList) {
    return videoList.filter(({ type, site }) => (type === "Trailer" || type === "Teaser") && site === "YouTube");
  };

  useEffect(() => {
    fetchDataFromServer(
      `https://api.themoviedb.org/3/movie/${movieId}?api_key=YOUR_API_KEY&append_to_response=cast,images,videos`
    )
      .then((data) => setMovieData(data))
      .catch((error) => console.error("Error fetching movie data:", error));
  }, [movieId]);

  if (!movieData) {
    return <div>Loading...</div>;
  }

  const {
    backdrop_path,
    poster_path,
    title,
    release_date,
    runtime,
    vote_average,
    genres,
    overview,
    credits: { cast, crew },
    videos: { results: videos },
  } = movieData;

  document.title = `${title} - CineScope`;

  return (
    <div className="movie-detail">
      <div
        className="backdrop-image"
        style={{
          background: `url(https://image.tmdb.org/t/p/w1280/${backdrop_path})`,
        }}
      ></div>
      <figure className="poster-box movie-poster">
        <img
          src={`https://image.tmdb.org/t/p/w500/${poster_path}`}
          className="img-cover"
          alt={title}
        />
      </figure>
      <div className="detail-box">
        <div className="detail-content">
          <h1 className="heading">{title}</h1>
          <div className="meta-list">
            <div className="meta-item">
              <img src={star} width="20" height="20" alt="rating" />
              <span className="span">{vote_average}</span>
            </div>
            <div className="separator"></div>
            <div className="meta-item">{`${runtime}m`}</div>
            <div className="separator"></div>
            <div className="meta-item">{release_date.slice(0, 4)}</div>
            <div className="meta-item card-badge">{getGenres(genres)}</div>
            <p className="overview">{overview}</p>
            <ul className="detail-list">
              <li className="list-item">
                <p className="list-name">Starring</p>
                <p>{getCasts(cast)}</p>
              </li>
              <li className="list-item">
                <p className="list-name">Directed By</p>
                <p>{getDirectors(crew)}</p>
              </li>
            </ul>
          </div>
        </div>
        <div className="title-wrapper">
          <h3 className="title-large">Trailers and clips</h3>
        </div>
        <div className="slider-list">
          <div className="slider-inner">
            {filterVideos(videos).map((video, index) => (
              <div key={index} className="video-card">
                {/* Render your video elements here */}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default MovieDetails;
