import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./moviedetails.css";
import star from "../../Images/star.png";
import { api_key, fetchDataFromServer } from "../../Api";

function MovieDetails() {
  const { movieId } = useParams();
  const [movieData, setMovieData] = useState(null);

  useEffect(() => {
    fetchDataFromServer(
      `https://api.themoviedb.org/3/movie/${movieId}?api_key=${api_key}&append_to_response=cast,videos,images,releases`
    )
      .then((movie) => setMovieData(movie))
      .catch((error) => console.error("Error fetching data:", error));
  }, [movieId]);

  const genreList = {
    asString: (genreIdList) => {
      const newGenreList = [];

      for (const genreId of genreIdList) {
        if (genres[genreId]) {
          newGenreList.push(genres[genreId].name);
        }
      }
      return newGenreList.join(", ");
    },
  };

  return (
    <div className="movie-detail">
      {movieData && (
        <>
          <div
            className="backdrop-image"
            style={{
              background: `url(https://image.tmdb.org/t/p/w1280/${movieData.backdrop_path})`,
            }}
          ></div>
          <figure className="poster-box movie-poster">
            <img
              src={`https://image.tmdb.org/t/p/w1280/${movieData.poster_path}`}
              className="img-cover"
              alt={movieData.title}
            />
          </figure>
          <div className="detail-box">
            <div className="detail-content">
              <h1 className="heading">{movieData.title}</h1>
              <div className="meta-list">
                <div className="meta-item">
                  <img src={star} width="20" height="20" alt="rating" />
                  <span className="span">{movieData.vote_average}</span>
                </div>
                <div className="separator"></div>
                <div className="meta-item">{movieData.runtime}m</div>
                <div className="separator"></div>
                <div className="meta-item">{movieData.release_date}</div>
                <div className="meta-item card-badge">
                  {movieData.releases.countries[0].certification}
                </div>
                <p className="genre">
                  {genreList.asString(movieData.genres.map((genre) => genre.id))}
                </p>
                <p className="overview">{movieData.overview}</p>
                <ul className="detail-list">
                  <div className="list-item">
                    <p className="list-name">Starring</p>
                    <p>{getCasts(movieData.casts.cast)}</p>
                  </div>
                  <div className="list-item">
                    <p className="list-name">Directed By</p>
                    <p>{getDirectors(movieData.casts.crew)}</p>
                  </div>
                </ul>
              </div>
            </div>
            <div className="title-wrapper">
              <h3 className="title-large">Trailers and clips</h3>
            </div>
            <div className="slider-list">
              <div className="slider-inner">
                {filterVideos(movieData.videos.results).map((video, index) => (
                  <div key={index} className="video-card">
                    <iframe
                      title={video.name}
                      width="100%"
                      height="100%"
                      src={`https://www.youtube.com/embed/${video.key}`}
                      frameborder="0"
                      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                      allowfullscreen
                    ></iframe>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default MovieDetails;
