import React, { useEffect, useState } from 'react';
import { api_key, fetchDataFromServer } from '../../Api';
import { Link } from 'react-router-dom';
import "./sidebar.css"
import menuclose from "../../Images/menu-close.png";
import menu from "../../Images/menu.png";

function Sidebar({ isActive }) {
  const [genrelist, setGenreList] = useState({});
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  
  const handleSidebarClick = () => {
    setIsSidebarOpen((prevState) => !prevState);
  };

  useEffect(() => {
    fetchDataFromServer(
      `https://api.themoviedb.org/3/genre/movie/list?api_key=${api_key}`,
      ({ genres }) => {
        const updatedGenreList = {};
        for (const { id, name } of genres) {
          updatedGenreList[id] = name;
        }
        setGenreList(updatedGenreList);
      }
    );
  }, []);

  return (
    <>
    <button className="menu-btn" onClick={handleSidebarClick}>
        <img
          src={menu}
          width="24"
          height="24"
          alt="open-menu"
          className={`open ${isSidebarOpen ? "" : "hide"}`}
        />
        <img
          src={menuclose}
          width="24"
          height="24"
          alt="close-menu"
          className={`close ${isSidebarOpen ? "" : "hide"}`}
        />
      </button>
    <nav className={`sidebar active ${isActive ? 'active' : ''}`} sidebar>
      <div className="sidebar-inner">
        <div className="sidebar-list">
          <p className="title">Genre</p>
          {Object.entries(genrelist).map(([genreId, genreName]) => (
            <Link
              key={genreId}
              to={`./movie-list.html?genre=${genreId}`}
              data-menu-close="true"
              className="sidebar-link"
            >
              {genreName}
            </Link>
          ))}

          <p className="title">Language</p>
          {/* Language links here */}
        </div>
        <div className="overlay " overlay menu-togglers></div>
      </div>
    </nav>
    </>
  );
}

export default Sidebar;
