import React from "react";
import { Link } from "react-router-dom";
import Logo from "../../Images/icon.png";
import search from "../../Images/search.png";
import close from "../../Images/close.png";
import menuclose from "../../Images/menu-close.png";
import menu from "../../Images/menu.png";
import "./header.css";

function Header({ isActive, handleToggleClick, handleSidebarClick }) {
  return (
    <div className="header">
      <Link to="/" className="logo">
        <img src={Logo} width="60" height="32" alt="Flicks home" />
        CINESCOPE
      </Link>
      <div className={`search-box ${isActive ? "active" : ""}`}>
        <div className="search-wrapper">
          <input
            type="text"
            name="search"
            aria-label="search movies"
            className="search-field"
            placeholder="search movies..."
            autoComplete="off"
          />
          <img
            src={search}
            width="24"
            height="24"
            alt="search"
            className="leading-icon"
          />
        </div>
        <button className="search-btn">
          <img src={close} width="24" height="24" alt="close search box" />
        </button>
      </div>
      <button className="search-btn">
        <img src={search} width="24" height="24" alt="open search box" />
      </button>

      <button className="menu-btn" onClick={handleSidebarClick}>
        <img
          src={menu}
          width="24"
          height="24"
          alt="open-menu"
          className={`open ${isActive ? "" : "hide"}`}
        />
        <img
          src={menuclose}
          width="24"
          height="24"
          alt="close-menu"
          className={`close ${isActive ? "" : "hide"}`}
        />
      </button>
    </div>
  );
}

export default Header;
