import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import star from "../../Images/star.png";
import sliderControl from "../../Images/slider-control.jpg";
import { api_key, fetchDataFromServer } from "../../Api";

function MovieList() {
  const location = useLocation();
  const genreId = new URLSearchParams(location.search).get("genreId");

  const [movieList, setMovieList] = useState([]);

  useEffect(() => {
    if (genreId) {
      fetchDataFromServer(
        `https://api.themoviedb.org/3/discover/movie?api_key=${api_key}&sort_by=popularity.desc&include_adult=false&page=1&with_genres=${genreId}`,
        handleMovieData
      );
    }
  }, [genreId]);

  const handleMovieData = function ({ results }) {
    setMovieList(results);
  };

  return (
    <section className="movie-list genre-list">
      <div className="title-wrapper">
        <h1 className="heading">All Fantasy Movies</h1>
      </div>
      <div className="grid-list">
        {movieList.map((movie, index) => (
          <div key={index} className="movie-card movie-card-small">
            <figure className="poster-box card-banner">
              <img
                src={sliderControl}
                alt="Marvel"
                className="img-cover"
              />
            </figure>
            <h4 className="title">{movie.title}</h4>
            <div className="meta-list">
              <div className="meta-item">
                <img
                  src={star}
                  alt="rating"
                  width="20"
                  height="20"
                  loading="lazy"
                />
                <span className="span">{movie.vote_average}</span>
              </div>
              <div className="card-badge">{movie.release_date.split("-")[0]}</div>
            </div>
            <Link to={`/details/${movie.id}`} className="card-btn" title={movie.title}></Link>
          </div>
        ))}
      </div>
      <button className="btn load-more">Load More</button>
    </section>
  );
}

export default MovieList;
